var link=document.querySelector(".js-feedback"),popup=document.querySelector(".feedback"),close=popup.querySelector(".feedback-close"),bg=document.querySelector(".feedback-bg");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("feedback-show"),bg.classList.add("feedback-show"),popup.classList.add("feedback-animation")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("feedback-show"),bg.classList.remove("feedback-show")}),bg.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("feedback-show"),bg.classList.remove("feedback-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("feedback-show")&&(popup.classList.remove("feedback-show"),bg.classList.remove("feedback-show"))});